import React from 'react';
import PropTypes from 'prop-types';

const UserImage = ({ userImage, username }) => (
    <a
      href={`https://github.com/${username}`}
      target="_blank"
      rel="noopener noreferrer"
      className="hexaHolder"
    >

      <div className="hexagon" style="{}">
        <div className="hexTop"></div>
        <div className="hexBottom"></div>
      </div>


      <div className="hexa">
        <div className="hex1">
          <div className="hex2">
            <img
              id="userImage"
              alt={`GitHub profile image for ${username}`}
              className="text-center border-4 border-solid border-white bg-white overflow-hidden dim"
              src={userImage}
            />
          </div>
        </div>
      </div>
    </a>
);

UserImage.propTypes = {
  username: PropTypes.string.isRequired,
  userImage: PropTypes.string.isRequired
};

export default UserImage;
